import { combineReducers } from 'redux'

const todoItems = (state = [], action) => {
  switch(action.type) {
    case 'ADD_TODO':
      return [
        ...state,
        {
          id: action.id,
          text: action.text,
          completed: false
        }
      ]
    default :
      return state
  }
}

export default combineReducers({
  todoItems,
})
